-- id 1 ~ 100 까지 더미 유저 생성 (닉네임 = 실제 사람 이름처럼)

  INSERT INTO users (
      id,
      email,
      password_hash,
      nickname,
      phone,
      birth_year,
      gender,
      user_type,
      status,
      email_verified,
      last_login_at,
      created_at,
      updated_at,
      deleted_at
  )
  SELECT * FROM (
      WITH RECURSIVE seq AS (
          SELECT 1 AS id
          UNION ALL
          SELECT id + 1
          FROM seq
          WHERE id < 100
      )
    SELECT
        s.id,
        CONCAT('user', LPAD(s.id, 3, '0'), '@example.com')              AS email,
        CONCAT('$2y$10$dummyhash_for_user_', LPAD(s.id, 3, '0'))         AS password_hash,

        -- 실제 사람 이름처럼 보이는 닉네임 (한국 이름 50개를 순환)
        ELT(
            ((s.id - 1) MOD 50) + 1,
            '김민수','이서준','박지우','최유진','정하준',
            '김서연','이도윤','박하늘','최지훈','정예린',
            '김준혁','이하은','박서준','최민지','정시우',
            '김지호','이수현','박예준','최다은','정준서',
            '김하린','이민재','박지원','최유나','정현우',
            '김나연','이채원','박도현','최서윤','정민호',
            '김예린','이연우','박현서','최지우','정아인',
            '김도윤','이하린','박시우','최가은','정도현',
            '김수아','이주원','박윤서','최서준','정수빈',
            '김하윤','이시윤','박민재','최예준','정유진'
        )                                                                AS nickname,

        CONCAT('010-0000-', LPAD(s.id, 4, '0'))                          AS phone,
        -- 1970 ~ 1999 사이 출생년
        (1970 + (s.id % 30))                                             AS birth_year,
        CASE s.id % 4
            WHEN 1 THEN 'M'
            WHEN 2 THEN 'F'
            WHEN 3 THEN 'O'
            ELSE 'N'
        END                                                              AS gender,
        CASE
            WHEN s.id <= 80 THEN 'GENERAL'
            WHEN s.id <= 95 THEN 'PROFESSIONAL'
            ELSE 'ADMIN'
        END                                                              AS user_type,
        CASE
            WHEN s.id % 20 = 0 THEN 'DELETED'
            WHEN s.id % 15 = 0 THEN 'SUSPENDED'
            WHEN s.id % 10 = 0 THEN 'INACTIVE'
            ELSE 'ACTIVE'
        END                                                              AS status,
        CASE WHEN s.id % 3 = 0 THEN 0 ELSE 1 END                         AS email_verified,
        DATE_SUB(NOW(), INTERVAL (s.id % 365) DAY)                       AS last_login_at,
        DATE_SUB(NOW(), INTERVAL (365 + s.id) DAY)                       AS created_at,
        DATE_SUB(NOW(), INTERVAL (s.id % 180) DAY)                       AS updated_at,
        CASE
            WHEN s.id % 20 = 0 THEN DATE_SUB(NOW(), INTERVAL (s.id % 365) DAY)
            ELSE NULL
        END                                                              AS deleted_at
    FROM seq AS s
  ) AS tmp;

