-- 템플릿 개수 변수에 저장
SET @template_cnt := (SELECT COUNT(*) FROM ReviewTemplate);

-- 2. 실제 Review 데이터 400건 삽입
-- centerId 1~100, 각 센터당 4개씩 = 400개
INSERT INTO reviews (
  center_id,
  user_id,
  rating,
  title,
  content,
  visit_date,
  is_verified,
  helpful_count,
  report_count,
  status,
  created_at,
  updated_at
)
SELECT
  c.id AS centerId,
  -- 1~100 사이의 userId를 순환하면서 부여 (필요시 이 부분 수정)
  (((c.id - 1) * 4 + n.n - 1) % 100) + 1 AS userId,
  t.rating,
  t.title,
  t.content,
  -- 최근 1년 이내 랜덤 방문일
  DATE_SUB(CURDATE(), INTERVAL FLOOR(RAND() * 365) DAY) AS visitDate,
  -- 약 40% 정도만 인증 리뷰
  (RAND() < 0.4) AS isVerified,
  -- 0~9 사이 랜덤 도움됨 카운트
  FLOOR(RAND() * 10) AS helpfulCount,
  -- 약 10%만 신고가 있고, 있을 경우 0~2 랜덤
  CASE
    WHEN RAND() < 0.1 THEN FLOOR(RAND() * 3)
    ELSE 0
  END AS reportCount,
  'ACTIVE' AS status,
  NOW() AS createdAt,
  NOW() AS updatedAt
FROM centers c
JOIN (
  -- 각 센터당 4개의 리뷰 생성
  SELECT 1 AS n UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4
) AS n
JOIN ReviewTemplate t
  -- (centerId, n) 조합을 템플릿 개수로 나눈 나머지로 템플릿 선택
  ON t.id = (((c.id - 1) * 4 + n.n - 1) % @template_cnt) + 1
WHERE c.id BETWEEN 1 AND 100;


